import React, { useState } from "react";
import { renderItem } from "../../utils/render";
import Button from "../Button/Button";

function ListItem({ item, handleActivate, handleDelete, index }) {
  const baseStyle = { textAlign: "left" };
  const activeStyle = item.active ? { color: "green", fontWeight: "bold" } : {};
  const itemStyle = { ...baseStyle, ...activeStyle };

  return (
    <li style={itemStyle}>
      {renderItem(item)}
      <Button
        title={item.active ? "Deactivate" : "Activate"}
        handleClick={() => handleActivate(index)}
      />
      <Button title="Delete" handleClick={() => handleDelete(index)} />
    </li>
  );
}

export default function List({ initialList = [] }) {
  const [list, setList] = useState(initialList);

  const handleActivate = (index) => {
    setList((prevList) =>
      prevList.map((item, idx) =>
        idx === index ? { ...item, active: !item.active } : item
      )
    );
  };

  const handleDelete = (index) => {
    setList((prevList) => prevList.filter((_, idx) => idx !== index));
  };

  return list.length ? (
    <ul>
      {list.map((item, index) => (
        <ListItem
          key={index}
          index={index}
          item={item}
          handleActivate={handleActivate}
          handleDelete={handleDelete}
        />
      ))}
    </ul>
  ) : null;
}
